sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(startDate)
	{
		var rightNow= new GlideDateTime().getLocalDate();
		var testDate = new GlideDateTime(startDate).getLocalDate();
		if(testDate.before(rightNow))
			{
				return true;
			}
		else
			{
				return false;
			}


	},
	isDateToday: function(startDate)
	{
		var rightNow= new GlideDateTime();
		var testDate = new GlideDateTime(startDate);

		var today= rightNow.getLocalDate();
		var isToday=testDate.getLocalDate();

		if(today.compareTo(isToday)==0)
			{
				return true;
			}
		else
			{
				return false;
			}



	},
    type: 'NeedItUtils'
};
